src/error/DetermineValidationOrIntrospec.source:
import {
    INTROSPECTION_DISABLED_ERROR,
    VALIDATION_ERROR,
} from '@dreamit/graphql-server-base'

/**
 * Determines if an error is a ValidationError or
 * IntrospectionDisabledError using the information in the error message
 * @param {unknown} error - An error
 * @returns {string} INTROSPECTION_DISABLED_ERROR if error is an IntrospectionDisabledError,
 * ValidationError otherwise
 */
export function determineValidationOrIntrospectionDisabledError(
    error: unknown,
): string {
    return error instanceof Error &&
        ((error.message.includes('introspection') &&
            error.message.includes('disabled')) ||
            error.message.includes('__Directive'))
        ? INTROSPECTION_DISABLED_ERROR
        : VALIDATION_ERROR
}

src/error/RemoveValidationRecommendation.source:
// Removes validation recommendations matching the defined recommendation text
import { GraphQLError } from 'graphql'

export const recommendationText = 'Did you mean'

export function removeValidationRecommendationsFromErrors(
    validationErrors: readonly GraphQLError[],
): readonly GraphQLError[] {
    for (const validationError of validationErrors) {
        if (validationError.message.includes(recommendationText)) {
            validationError.message = validationError.message.slice(
                0,
                Math.max(
                    0,
                    validationError.message.indexOf(recommendationText),
                ),
            )
        }
    }
    return validationErrors
}

src/logger/CreateLogEntry.ts.source:
import { LogEntry, LogEntryInput, LogLevel } from '@dreamit/graphql-server-base'
import { GraphQLError } from 'graphql'
import { createISOTimestamp } from './CreateTimestamp'
import { sanitizeMessage } from './SanitizeMessage'

export function createLogEntry(logEntryInput: LogEntryInput): LogEntry {
    const {
        dateFunction,
        loggerName,
        logMessage,
        loglevel,
        serviceName,
        context,
        error,
        customErrorName,
    } = logEntryInput

    const logEntry: LogEntry = {
        level: loglevel ?? LogLevel.info,
        logger: loggerName ?? 'fallback-logger',
        message: sanitizeMessage(logMessage),
        serviceName: serviceName ?? 'fallback-service',
        timestamp: createISOTimestamp(dateFunction),
    }

    // If there is a serviceName in the context, use it as serviceName for the LogEntry
    const contextRecord = context as Record<string, unknown>
    if (contextRecord && contextRecord.serviceName) {
        logEntry.serviceName = contextRecord.serviceName as string
        if (
            loglevel === LogLevel.error &&
            contextRecord.serviceName !== serviceName
        ) {
            logEntry.level = LogLevel.warn
        }
    }
    if (contextRecord && contextRecord.query) {
        logEntry.query = contextRecord.query as string
    }

    if (error) {
        logEntry.errorName = customErrorName ?? error.name
        logEntry.message =
            `${logEntry.message} ${sanitizeMessage(error.message)}`.trim()
        if (error.stack) {
            logEntry.stacktrace = sanitizeMessage(error.stack)
        }

        if (error instanceof GraphQLError) {
            if (error.extensions.query) {
                logEntry.query = error.extensions.query as string
            } else if (error.source && error.source.body) {
                logEntry.query = error.source.body
            } else if (error.nodes) {
                logEntry.query = JSON.stringify(error.nodes)
            }

            if (error.extensions.serviceName) {
                logEntry.serviceName = error.extensions.serviceName as string
                logEntry.level =
                    error.extensions.serviceName === serviceName
                        ? LogLevel.error
                        : LogLevel.warn
            }

            if (error.extensions.exception) {
                logEntry.stacktrace = sanitizeMessage(
                    error.extensions.exception as string,
                )
            }
        }
    }
    return logEntry
}

