import { DateFunction } from '@dreamit/funpara'
import {
    LogEntry,
    LogEntryInput,
    Logger,
    LogLevel,
} from '@dreamit/graphql-server-base'
import { Console } from 'node:console'
import { createLogEntry } from './CreateLogEntry'
import { truncateLogMessage } from './TruncateLogMessage'

/**
 * Logger implementation that outputs log entries as text to console.
 */
export class TextLogger implements Logger {
    loggerName: string
    serviceName: string
    debugEnabled: boolean
    truncateLimit: number
    truncatedText: string
    loggerConsole: Console

    /**
     * Creates a new instance of Logger.
     * @param {string} loggerName - The logger name of the logger.
     * Will be used in output if not empty.
     * @param {string} serviceName - The service name of the logger.
     * Used to identify the graphql server and can be used to differentiate
     * it from remote graphql services like in a gateway setup.
     * Will be output to "serviceName" field in JSON.
     * @param {boolean} debugEnabled - If debug output should be enabled
     * @param {number} truncateLimit - The length of the message before the message gets truncated.
     * Default: undefined/0 (off).
     * @param {string} truncatedText - The text to display if a message is truncated.
     * @param {Console} loggerConsole - The Console to use for logging
     */
    constructor(
        loggerName: string,
        serviceName: string,
        debugEnabled = false,
        truncateLimit = 0,
        truncatedText = '_TRUNCATED_',
        loggerConsole: Console = new Console(process.stdout, process.stderr),
    ) {
        this.loggerName = loggerName
        this.serviceName = serviceName
        this.debugEnabled = debugEnabled
        this.truncateLimit = truncateLimit
        this.truncatedText = truncatedText
        this.loggerConsole = loggerConsole
    }

    debug(
        logMessage: string,
        context?: unknown,
        dateFunction?: DateFunction,
    ): void {
        if (this.debugEnabled) {
            this.logMessage({
                context,
                dateFunction,
                logMessage,
                loglevel: LogLevel.debug,
            })
        }
    }

    error(
        logMessage: string,
        error: Error,
        customErrorName: string,
        context?: unknown,
        dateFunction?: DateFunction,
    ): void {
        this.logMessage({
            context,
            customErrorName,
            dateFunction,
            error,
            logMessage,
            loglevel: LogLevel.error,
        })
    }

    info(
        logMessage: string,
        context?: unknown,
        dateFunction?: DateFunction,
    ): void {
        this.logMessage({
            context,
            dateFunction,
            logMessage,
            loglevel: LogLevel.info,
        })
    }

    warn(
        logMessage: string,
        context?: unknown,
        dateFunction?: DateFunction,
    ): void {
        this.logMessage({
            context,
            dateFunction,
            logMessage,
            loglevel: LogLevel.warn,
        })
    }

    logMessage(logEntryInput: LogEntryInput): void {
        const {
            dateFunction,
            logMessage,
            loglevel,
            error,
            customErrorName,
            context,
        } = logEntryInput

        const logEntry: LogEntry = createLogEntry({
            context,
            customErrorName,
            dateFunction,
            error,
            logMessage,
            loggerName: this.loggerName,
            loglevel: loglevel,
            serviceName: this.serviceName,
        })
        this.loggerConsole.log(
            this.prepareLogOutput(
                truncateLogMessage(
                    logEntry,
                    this.truncateLimit,
                    this.truncatedText,
                ),
                context,
            ),
        )
    }

    /**
     * Prepares the text used in the log output.
     * Can be overwritten if it does not match expected output format.
     * @param {LogEntry} logEntry - The extracted log information.
     * @param {unknown} context - The context information
     */
    // eslint-disable-next-line no-unused-vars
    prepareLogOutput(logEntry: LogEntry, context?: unknown): string {
        return (
            `${logEntry.timestamp} [${logEntry.level.toUpperCase()}]` +
            `${this.loggerName}-${this.serviceName} :` +
            logEntry.message +
            (logEntry.stacktrace ? ` ${logEntry.stacktrace}` : '')
        )
    }
}

src/logger/TruncateLogMessage.ts.source:
import { LogEntry } from '@dreamit/graphql-server-base'

export function truncateLogMessage(
    logEntry: LogEntry,
    truncateLimit = 0,
    truncatedText = '_TRUNCATED_',
): LogEntry {
    if (
        truncateLimit > 0 &&
        logEntry.message.length > truncateLimit + truncatedText.length
    ) {
        logEntry.message =
            truncateLimit > truncatedText.length
                ? logEntry.message.slice(
                      0,
                      truncateLimit - truncatedText.length,
                  ) + truncatedText
                : logEntry.message.slice(0, truncateLimit)
    }
    return logEntry
}

src/metrics/IncreaseFetchOrGraphQLErrorM.source:
import { determineGraphQLOrFetchError } from '../error/DetermineGraphQLOrFetchError'
import { GraphQLServerOptions } from '../server/GraphQLServerOptions'

/**
 * Increases the error metric with either a FetchError or GraphQLError label
 * @param {unknown} error - An error
 * @param {unknown} context - The request context
 * @param {GraphQLServerOptions} serverOptions - The GraphQLServerOptions
 */
export function increaseFetchOrGraphQLErrorMetric(
    error: unknown,
    serverOptions: GraphQLServerOptions,
    context: unknown,
): void {
    const { collectErrorMetricsFunction, logger } = serverOptions
    if (logger) {
        logger.debug(
            'Calling increaseFetchOrGraphQLErrorMetric' +
                ` with error ${error} and errorIsFetch ${error instanceof Error}`,
            context,
        )
    }
    if (collectErrorMetricsFunction) {
        collectErrorMetricsFunction({
            context,
            error,
            errorName: determineGraphQLOrFetchError(error),
            serverOptions,
        })
    }
}

