import { GraphQLExecutionResult } from '@dreamit/graphql-server-base'
import { GraphQLError } from 'graphql'

export function getFirstErrorFromExecutionResult(
    result: GraphQLExecutionResult,
): GraphQLError {
    if (
        result.executionResult.errors &&
        result.executionResult.errors.length > 0
    ) {
        return result.executionResult.errors[0]
    }
    return new GraphQLError('No error found in ExecutionResult!', {})
}

src/response/SendResponse.ts.source:
import { ResponseParameters } from '@dreamit/graphql-server-base'
import { GraphQLError } from 'graphql'
import { Buffer } from 'node:buffer'
import { getResponseSchemaValidationErrors } from '../validation/GetResponseSchemaValidationErrors'

/**
 * Default implementation of sendResponse function
 */
export function sendResponse(responseParameters: ResponseParameters): void {
    const {
        customHeaders,
        context,
        logger,
        response,
        statusCode,
        formatErrorFunction,
        responseEndChunkFunction,
        responseStandardSchema,
    } = responseParameters
    let executionResult = responseParameters.executionResult
    logger.debug(
        `Preparing response with executionResult ${JSON.stringify(executionResult)}` +
            `, status code ${statusCode} and custom headers ${JSON.stringify(customHeaders)}` +
            `, and context ${context}`,
        context,
    )

    if (responseStandardSchema) {
        try {
            const validationErrors = getResponseSchemaValidationErrors(
                responseStandardSchema,
                executionResult,
            )
            if (validationErrors) {
                executionResult = {
                    errors: validationErrors.map(
                        (error) => new GraphQLError(error.message, {}),
                    ),
                }
            }
        } catch (error: unknown) {
            logger.error(
                `An error occurred while validating the response: ${error}`,
                error as TypeError,
                'ResponseValidationError',
                context,
            )
        }
    }

    if (executionResult && executionResult.errors && formatErrorFunction) {
        executionResult.errors.map((element) => formatErrorFunction(element))
    }
    if (statusCode) {
        response.statusCode = statusCode
    }
    response.setHeader('Content-Type', 'application/json; charset=utf-8')
    if (customHeaders) {
        for (const [key, value] of Object.entries(customHeaders)) {
            logger.debug(`Set custom header ${key} to ${value}`, context)
            response.setHeader(key, String(value))
        }
    }

    if (responseEndChunkFunction) {
        response.end(responseEndChunkFunction(executionResult))
    } else {
        response.end(Buffer.from(JSON.stringify(executionResult), 'utf8'))
    }
}

src/server/DefaultGraphQLServerOptions.t.source:
import {
    GraphQLExecutionResult,
    GraphQLRequestInfo,
    GraphQLServerRequest,
    GraphQLServerResponse,
    Logger,
    MetricsClient,
    ResponseParameters,
} from '@dreamit/graphql-server-base'
import {
    ExecutionResult,
    GraphQLError,
    GraphQLFieldResolver,
    GraphQLFormattedError,
    GraphQLSchema,
    GraphQLTypeResolver,
    TypeInfo,
    ValidationRule,
    execute,
    parse,
    specifiedRules,
    validate,
    validateSchema,
} from 'graphql'
import { Buffer } from 'node:buffer'
import { TextLogger } from '../logger/TextLogger'
import { SimpleMetricsClient } from '../metrics/SimpleMetricsClient'
import { extractInformationFromRequest } from '../request/ExtractInformationFromRequest'
import { sendResponse } from '../response/SendResponse'
import { StandaloneResponseParameters } from '../response/StandaloneResponseParameters'
import { StandardSchemaV1 } from '../validation/StandardSchemaV1'
import { GraphQLServerOptions } from './GraphQLServerOptions'

export const defaultGraphqlExecutionErrorMessage =
    'While processing the request a GraphQL execution error occurred: '
export const defaultExecutionResultErrorMessage =
    'While processing the request the following error occurred: '
export const defaultValidationErrorMessage =
    'While processing the request the following validation error occurred: '

export const fallbackTextLogger: Logger = new TextLogger(
    'fallback-logger',
    'fallback-service',
)
export const invalidSchemaResponse: GraphQLExecutionResult = {
    executionResult: {
        errors: [
            new GraphQLError(
                'Request cannot be processed. Schema in GraphQL server is invalid.',
                {},
            ),
        ],
    },
    statusCode: 500,
}

export const noOpStandardSchema: StandardSchemaV1 = {
    '~standard': {
        validate: (value: unknown) => ({ value }),
        vendor: 'no-op',
        version: 1,
    },
}

export class DefaultGraphQLServerOptions implements GraphQLServerOptions {
    logger: Logger = fallbackTextLogger
    extractInformationFromRequest: (
        request: GraphQLServerRequest,
    ) => GraphQLRequestInfo = extractInformationFromRequest
    sendResponse: (responseParameters: ResponseParameters) => void =
        sendResponse
    metricsClient: MetricsClient = new SimpleMetricsClient()
    formatErrorFunction = defaultFormatErrorFunction
    collectErrorMetricsFunction = defaultCollectErrorMetrics
    schemaValidationFunction = validateSchema
    parseFunction = parse
    defaultValidationRules = specifiedRules
    customValidationRules: readonly ValidationRule[] = []
    removeValidationRecommendations = true
    reassignAggregateError = false
    validateFunction = validate
    contextFunction = defaultContextFunction
    executeFunction = execute
    extensionFunction = defaultExtensions
    shouldUpdateSchemaFunction = defaultShouldUpdateSchema
    validationTypeInfo?: TypeInfo
    validationOptions?: { maxErrors?: number }
    rootValue?: unknown
    fieldResolver?: null | undefined | GraphQLFieldResolver<unknown, unknown>
    typeResolver?: null | undefined | GraphQLTypeResolver<unknown, unknown>
    schema?: GraphQLSchema
    invalidSchemaResponse = invalidSchemaResponse
    missingQueryParameterResponse = defaultMissingQueryParameterResponse
    methodNotAllowedResponse = defaultMethodNotAllowedResponse
    onlyQueryInGetRequestsResponse = defaultOnlyQueryInGetRequestsResponse
    validationErrorMessage = defaultValidationErrorMessage
    executionResultErrorMessage = defaultExecutionResultErrorMessage
    graphqlExecutionErrorMessage = defaultGraphqlExecutionErrorMessage
    responseEndChunkFunction = defaultResponseEndChunkFunction
    fetchErrorMessage?: string
    adjustGraphQLExecutionResult?: (
        parameters: StandaloneResponseParameters,
    ) => GraphQLExecutionResult
    responseStandardSchema: StandardSchemaV1 = noOpStandardSchema
}

/**
 * Default format error function to format error if necessary.
 * Default behaviour: Calls toJSON function of error. Can be set in options.
 * @param {GraphQLError} error - The error to be formatted
 */
export function defaultFormatErrorFunction(
    error: GraphQLError,
): GraphQLFormattedError {
    return error.toJSON()
}

/**
 * Default context function to store information in context for further use.
 * Default behaviour: return request object. Can be set in options.
 * @param contextParameters - The context parameters
 */
export function defaultContextFunction(contextParameters: {
    serverOptions: GraphQLServerOptions
    request?: GraphQLServerRequest
    response?: GraphQLServerResponse
}): unknown {
    const { serverOptions, request, response } = contextParameters
    const logger = serverOptions.logger
    if (logger) {
        logger.debug(
            'Calling defaultRequestResponseContextFunction with ' +
                `request ${request} and response ${response}`,
            request,
        )
    }
    return request
}

/**
 * Default extension function that can be used
 * to fill extensions field of GraphQL response. Can be set in options.
 * @param extensionParameters - The extensions parameters
 * @returns {Record<string, unknown>}
 * A key-value map to be added as extensions in response
 */
export function defaultExtensions(extensionParameters: {
    requestInformation: GraphQLRequestInfo
    executionResult: ExecutionResult
    serverOptions: GraphQLServerOptions
    context?: unknown
}): Record<string, unknown> | undefined {
    const { requestInformation, executionResult, serverOptions, context } =
        extensionParameters
    const logger = serverOptions.logger
    if (logger) {
        logger.debug(
            `Calling defaultExtensions for requestInfo ${JSON.stringify(requestInformation)}` +
                ` and executionResult ${JSON.stringify(executionResult)}`,
            context,
        )
    }

    return undefined
}

/**
 * Default collect error metrics function. Can be set in options.
 * @param errorParameters - The error parameters
 */
export function defaultCollectErrorMetrics(errorParameters: {
    errorName: string
    error: unknown
    serverOptions: GraphQLServerOptions
    context?: unknown
}): void {
    const { errorName, error, serverOptions, context } = errorParameters
    const { logger, metricsClient } = serverOptions
    if (logger) {
        logger.debug(
            `Calling defaultCollectErrorMetrics with error ${error} and errorName ${errorName}`,
            context,
        )
    }
    if (metricsClient) {
        metricsClient.increaseErrors(errorName, context)
    }
}

/**
 * Determines whether a schema update should be executed.
 * Default behaviour: If schema is undefined return false.
 * @param {GraphQLSchema} schema - The new schema to use as updated schema.
 * @returns {boolean} True if schema should be updated, false if not
 */
export function defaultShouldUpdateSchema(schema?: GraphQLSchema): boolean {
    return !!schema
}

/**
 * Return a default error message if used method is not allowed by GraphQLServer
 * @param {string} method - The actual used method
 * @returns {GraphQLExecutionResult} A MethodNotAllowed response
 */
export function defaultMethodNotAllowedResponse(
    method?: string,
): GraphQLExecutionResult {
    return {
        customHeaders: { allow: 'GET, POST' },
        executionResult: {
            errors: [
                new GraphQLError(
                    'GraphQL server only supports GET and POST requests.' +
                        ` Got ${method}`,
                    {},
                ),
            ],
        },
        statusCode: 405,
    }
}

/**
 * Return a default error message if no query information is found in body or URL parameter
 * @param {string} method - The actual used method
 * @returns {GraphQLExecutionResult} A MissingQueryParameter response
 */
export function defaultMissingQueryParameterResponse(
    method?: string,
): GraphQLExecutionResult {
    return {
        executionResult: {
            errors: [
                new GraphQLError(
                    'Request cannot be processed. No query was found ' +
                        `in parameters or body. Used method is ${method}`,
                    {},
                ),
            ],
        },
        statusCode: 400,
    }
}

/**
 * Return a default error message if an unsupported operation is used in GET requests.
 * Default: Only supported operation is "query"
 * @param {string} operation - The actual used operation
 * @returns {GraphQLExecutionResult} A OnlyQueryInGetRequestsResponse response
 */
export function defaultOnlyQueryInGetRequestsResponse(
    operation?: string,
): GraphQLExecutionResult {
    return {
        customHeaders: { allow: 'POST' },
        executionResult: {
            errors: [
                new GraphQLError(
                    'Only "query" operation is allowed in "GET" requests.' +
                        ` Got: "${operation}"`,
                    {},
                ),
            ],
        },
        statusCode: 405,
    }
}

/**
 * Default response.end chunk function to adjust chunk/Body if necessary.
 * Default behavior: Create Buffer from stringified executionResult
 * @param {GraphQLError} error - The error to be formatted
 */
export function defaultResponseEndChunkFunction(
    executionResult: ExecutionResult | undefined,
): unknown {
    return Buffer.from(JSON.stringify(executionResult), 'utf8')
}

src/server/GetRequestInformation.ts.source:
import {
    GraphQLExecutionResult,
    GraphQLRequestInfo,
    GraphQLServerRequest,
    METHOD_NOT_ALLOWED_ERROR,
} from '@dreamit/graphql-server-base'
import { extractInformationFromRequest } from '../request/ExtractInformationFromRequest'
import { requestCouldNotBeProcessed } from '../request/RequestConstants'
import { getFirstErrorFromExecutionResult } from '../response/GraphQLExecutionResult'
import {
    defaultCollectErrorMetrics,
    defaultMethodNotAllowedResponse,
    fallbackTextLogger,
} from './DefaultGraphQLServerOptions'
import { GraphQLServerOptions } from './GraphQLServerOptions'

export function getRequestInformation(
    request: GraphQLServerRequest,
    context: unknown,
    options: GraphQLServerOptions,
): GraphQLRequestInfo | GraphQLExecutionResult {
    const logger = options.logger ?? fallbackTextLogger
    const collectErrorMetricsFunction =
        options.collectErrorMetricsFunction ?? defaultCollectErrorMetrics
    const methodNotAllowedResponse =
        options.methodNotAllowedResponse ?? defaultMethodNotAllowedResponse
    const extractInformationFromRequestFunction =
        options.extractInformationFromRequest ?? extractInformationFromRequest

    // Reject requests that do not use GET and POST methods.
    if (request.method !== 'GET' && request.method !== 'POST') {
        const response = methodNotAllowedResponse(request.method)
        const error = getFirstErrorFromExecutionResult(response)
        logger.error(
            requestCouldNotBeProcessed,
            error,
            METHOD_NOT_ALLOWED_ERROR,
            context,
        )
        collectErrorMetricsFunction({
            context,
            error,
            errorName: METHOD_NOT_ALLOWED_ERROR,
            serverOptions: options,
        })
        return response
    }

    // Extract graphql request information (query, variables, operationName) from request
    const requestInformation = extractInformationFromRequestFunction(request)
    logger.debug(
        `Extracted request information is ${JSON.stringify(requestInformation)}`,
        context,
    )
    return requestInformation
}

src/server/GraphQLServer.ts.source:
import {
    FETCH_ERROR,
    GRAPHQL_ERROR,
    GraphQLExecutionResult,
    GraphQLRequestInfo,
    GraphQLServerRequest,
    GraphQLServerResponse,
    INVALID_SCHEMA_ERROR,
    METHOD_NOT_ALLOWED_ERROR,
    MISSING_QUERY_PARAMETER_ERROR,
    MetricsClient,
    SCHEMA_VALIDATION_ERROR,
    SYNTAX_ERROR,
    isAggregateError,
    isGraphQLServerRequest,
} from '@dreamit/graphql-server-base'
import {
    DocumentNode,
    GraphQLError,
    GraphQLSchema,
    Source,
    getOperationAST,
} from 'graphql'
import { determineGraphQLOrFetchError } from '../error/DetermineGraphQLOrFetchError'
import { determineValidationOrIntrospectionDisabledError } from '../error/DetermineValidationOrIntrospectionDisabledError'
import { removeValidationRecommendationsFromErrors } from '../error/RemoveValidationRecommendationsFromErrors'
import { increaseFetchOrGraphQLErrorMetric } from '../metrics/IncreaseFetchOrGraphQLErrorMetric'
import { SimpleMetricsClient } from '../metrics/SimpleMetricsClient'
import { requestCouldNotBeProcessed } from '../request/RequestConstants'
import { getFirstErrorFromExecutionResult } from '../response/GraphQLExecutionResult'
import { getRequestInformation as getRequestInformationFunction } from '../server/GetRequestInformation'
import { DefaultGraphQLServerOptions } from './DefaultGraphQLServerOptions'
import { GraphQLServerOptions } from './GraphQLServerOptions'

const defaultOptions = new DefaultGraphQLServerOptions()

export class GraphQLServer {
    options: DefaultGraphQLServerOptions = new DefaultGraphQLServerOptions()
    schemaValidationErrors: readonly GraphQLError[] = []

    constructor(optionsParameter?: GraphQLServerOptions) {
        this.setOptions(optionsParameter)
    }

    setOptions(newOptions?: GraphQLServerOptions): void {
        this.options = { ...defaultOptions, ...newOptions }
        this.setMetricsClient(
            newOptions?.metricsClient ?? new SimpleMetricsClient(),
        )
        this.setSchema(newOptions?.schema)
    }

    /**
     * Sets a metrics client for to be used in the GraphQLServer.
     * @param {MetricsClient} metricsClient - The metrics client to use in the GraphQLServer
     */
    setMetricsClient(metricsClient: MetricsClient): void {
        this.options.metricsClient = metricsClient
        this.options.metricsClient.setAvailability(
            this.isValidSchema(this.options.schema) ? 1 : 0,
        )
    }

    getSchema(): GraphQLSchema | undefined {
        return this.options.schema
    }

    isValidSchema(schema?: GraphQLSchema): boolean {
        return schema ? this.schemaValidationErrors.length === 0 : false
    }

    setSchema(schema?: GraphQLSchema): void {
        const logger = this.options.logger
        logger.info('Trying to set graphql schema')
        logger.debug(`Schema is  ${JSON.stringify(schema)}`)
        if (this.options.shouldUpdateSchemaFunction(schema)) {
            this.options.schema = schema
            // Validate schema
            if (this.options.schema) {
                this.schemaValidationErrors =
                    this.options.schemaValidationFunction(this.options.schema)
                if (this.schemaValidationErrors.length > 0) {
                    logger.warn(
                        'Schema validation failed with errors. Please check the GraphQL schema and fix potential issues.',
                    )
                    for (const error of this.schemaValidationErrors) {
                        logger.error(
                            'A schema validation error occurred: ',
                            error,
                            SCHEMA_VALIDATION_ERROR,
                        )
                        this.options.collectErrorMetricsFunction({
                            error,
                            errorName: SCHEMA_VALIDATION_ERROR,
                            serverOptions: this.options,
                        })
                    }
                }
            }
        } else {
            logger.warn(
                'Schema update was rejected because condition set in "shouldUpdateSchema" check was not fulfilled.',
            )
        }
        this.options.metricsClient.setAvailability(
            this.isValidSchema(this.options.schema) ? 1 : 0,
        )
    }

    getSchemaValidationErrors(): ReadonlyArray<GraphQLError> | undefined {
        return this.schemaValidationErrors
    }

    // Gets the Content-Type of the metrics for use in the response headers
    getMetricsContentType(): string {
        return this.options.metricsClient.getMetricsContentType()
    }

    // Gets the metrics for use in the response body.
    async getMetrics(): Promise<string> {
        return await this.options.metricsClient.getMetrics()
    }

    /**
     * Executes a given request and returns an execution result
     * @param {GraphQLServerRequest | GraphQLRequestInfo} request -
     * The server request or request information
     * @param {GraphQLServerResponse} response - If set sends a response, else not
     * @returns {GraphQLExecutionResult} The execution result
     */
    async handleRequest(
        request: GraphQLServerRequest | GraphQLRequestInfo,
        response?: GraphQLServerResponse,
    ): Promise<GraphQLExecutionResult> {
        const {
            adjustGraphQLExecutionResult,
            contextFunction,
            formatErrorFunction,
            logger,
            metricsClient,
            sendResponse,
            responseEndChunkFunction,
            responseStandardSchema,
        } = this.options

        const context = contextFunction({
            request: isGraphQLServerRequest(request) ? request : undefined,
            response,
            serverOptions: this.options,
        })
        metricsClient.increaseRequestThroughput(context)
        const requestInformation = isGraphQLServerRequest(request)
            ? this.getRequestInformation(request, context)
            : request
        if ('query' in requestInformation && context) {
            const contextAsRecord = context as Record<string, unknown>
            contextAsRecord.query = requestInformation.query
        }
        if (
            isGraphQLServerRequest(request) &&
            'executionResult' in requestInformation
        ) {
            let result: GraphQLExecutionResult = {
                customHeaders: requestInformation.customHeaders,
                executionResult: requestInformation.executionResult,
                statusCode: requestInformation.statusCode,
            }

            if (adjustGraphQLExecutionResult) {
                result = adjustGraphQLExecutionResult({
                    context,
                    executionResult: result,
                    formatErrorFunction,
                    logger,
                    request: request,
                })
            }

            if (response && isGraphQLServerRequest(request)) {
                sendResponse({
                    context,
                    customHeaders: result.customHeaders,
                    executionResult: result.executionResult,
                    formatErrorFunction,
                    logger,
                    request,
                    response,
                    responseEndChunkFunction,
                    responseStandardSchema,
                    statusCode: result.statusCode,
                })
            }
            return result
        }

        let result = await this.executeRequestWithInfo(
            requestInformation as GraphQLRequestInfo,
            context,
            isGraphQLServerRequest(request) ? request.method : undefined,
        )

        if (adjustGraphQLExecutionResult) {
            result = adjustGraphQLExecutionResult({
                context,
                executionResult: result,
                formatErrorFunction,
                logger,
                request: isGraphQLServerRequest(request) ? request : undefined,
            })
        }

        if (response) {
            sendResponse({
                context,
                customHeaders: result.customHeaders,
                executionResult: result.executionResult,
                formatErrorFunction,
                logger,
                request: isGraphQLServerRequest(request) ? request : undefined,
                response,
                responseEndChunkFunction,
                responseStandardSchema,
                statusCode: result.statusCode,
            })
        }
        return result
    }

    /**
     * Extracts the request information from a given request
     * @deprecated Use getRequestInformation function instead
     * @param {GraphQLServerRequest} request - The server request
     * @param {unknown} context - The context to be used
     * @returns {GraphQLRequestInfo | GraphQLExecutionResult} The request information
     * or an execution result if an error occurred
     */
    getRequestInformation(
        request: GraphQLServerRequest,
        context: unknown,
    ): GraphQLRequestInfo | GraphQLExecutionResult {
        return getRequestInformationFunction(request, context, this.options)
    }

    async executeRequestWithInfo(
        requestInformation: GraphQLRequestInfo,
        context?: unknown,
        requestMethod?: string,
    ): Promise<GraphQLExecutionResult> {
        const {
            logger,
            metricsClient,
            invalidSchemaResponse,
            missingQueryParameterResponse,
            onlyQueryInGetRequestsResponse,
            collectErrorMetricsFunction,
            schema,
            parseFunction,
            validateFunction,
            defaultValidationRules,
            customValidationRules,
            validationOptions,
            validationTypeInfo,
            executeFunction,
            rootValue,
            fieldResolver,
            typeResolver,
            extensionFunction,
            reassignAggregateError,
            removeValidationRecommendations,
            validationErrorMessage,
            executionResultErrorMessage,
            graphqlExecutionErrorMessage,
            fetchErrorMessage,
        } = this.options

        // Reject requests if schema is invalid
        if (!schema || !this.isValidSchema(schema)) {
            metricsClient.setAvailability(0)
            const error = getFirstErrorFromExecutionResult(
                invalidSchemaResponse,
            )
            logger.error(
                requestCouldNotBeProcessed,
                error,
                INVALID_SCHEMA_ERROR,
                context,
            )
            collectErrorMetricsFunction({
                context,
                error,
                errorName: INVALID_SCHEMA_ERROR,
                serverOptions: this.options,
            })
            return { ...invalidSchemaResponse, ...requestInformation }
        } else {
            metricsClient.setAvailability(1)
        }

        if (!requestInformation.query && requestInformation.error) {
            logger.error(
                requestCouldNotBeProcessed,
                requestInformation.error.graphQLError,
                GRAPHQL_ERROR,
                context,
            )
            collectErrorMetricsFunction({
                context,
                error: requestInformation.error,
                errorName: GRAPHQL_ERROR,
                serverOptions: this.options,
            })
            return {
                executionResult: {
                    errors: [requestInformation.error.graphQLError],
                },
                requestInformation: requestInformation,
                statusCode: requestInformation.error.statusCode,
            }
        }
        // Reject request if no query parameter is provided
        else if (!requestInformation.query) {
            const response = missingQueryParameterResponse(requestMethod)
            const error = getFirstErrorFromExecutionResult(response)
            logger.error(
                requestCouldNotBeProcessed,
                error,
                MISSING_QUERY_PARAMETER_ERROR,
                context,
            )
            collectErrorMetricsFunction({
                context,
                error,
                errorName: MISSING_QUERY_PARAMETER_ERROR,
                serverOptions: this.options,
            })
            return { ...response, ...requestInformation }
        }

        // Parse given GraphQL source into a document (parse(query) function)
        let documentAST: DocumentNode
        try {
            documentAST = parseFunction(
                new Source(requestInformation.query, 'GraphQL request'),
            )
        } catch (syntaxError: unknown) {
            logger.error(
                requestCouldNotBeProcessed,
                syntaxError as GraphQLError,
                SYNTAX_ERROR,
                context,
            )
            collectErrorMetricsFunction({
                context,
                error: syntaxError,
                errorName: SYNTAX_ERROR,
                serverOptions: this.options,
            })
            return {
                executionResult: { errors: [syntaxError as GraphQLError] },
                requestInformation: requestInformation,
                statusCode: 400,
            }
        }
        logger.debug(
            `Parsing query into document succeeded with document: ${JSON.stringify(documentAST)}`,
            context,
        )

        /**
         * Validate document against schema (
         * validate(schema, document, rules) function). Return 400 for errors
         */
        const validationErrors = validateFunction(
            schema,
            documentAST,
            [...defaultValidationRules, ...customValidationRules],
            validationOptions,
            validationTypeInfo,
        )
        if (validationErrors.length > 0) {
            logger.debug(
                `One or more validation errors occurred: ${JSON.stringify(validationErrors)}`,
                context,
            )
            for (const validationError of validationErrors) {
                const errorName =
                    determineValidationOrIntrospectionDisabledError(
                        validationError,
                    )
                logger.error(
                    validationErrorMessage,
                    validationError,
                    errorName,
                    context,
                )
                collectErrorMetricsFunction({
                    context,
                    error: validationError,
                    errorName,
                    serverOptions: this.options,
                })
            }
            return {
                executionResult: {
                    errors: removeValidationRecommendations
                        ? removeValidationRecommendationsFromErrors(
                              validationErrors,
                          )
                        : validationErrors,
                },
                requestInformation: requestInformation,
                statusCode: 400,
            }
        }

        /**
         * Reject request if get method is used for non-query(mutation) requests.
         * Check with getOperationAST(document, operationName) function.
         * Return 405 if that is the case
         */
        const operationAST = getOperationAST(
            documentAST,
            requestInformation.operationName,
        )
        if (
            requestMethod === 'GET' &&
            operationAST &&
            operationAST.operation !== 'query'
        ) {
            const response = onlyQueryInGetRequestsResponse(
                operationAST.operation,
            )
            const error = getFirstErrorFromExecutionResult(response)
            logger.error(
                requestCouldNotBeProcessed,
                error,
                METHOD_NOT_ALLOWED_ERROR,
                context,
            )
            collectErrorMetricsFunction({
                context,
                error,
                errorName: METHOD_NOT_ALLOWED_ERROR,
                serverOptions: this.options,
            })
            return { ...response, ...requestInformation }
        }

        /**
         * Perform execution
         * (execute(schema, document, variables, operationName, resolvers) function).
         * Return 400 if errors are available
         */
        try {
            const executionResult = await executeFunction({
                contextValue: context,
                document: documentAST,
                fieldResolver: fieldResolver,
                operationName: requestInformation.operationName,
                rootValue: rootValue,
                schema: schema,
                typeResolver: typeResolver,
                variableValues: requestInformation.variables,
            })

            const extensionsResult = extensionFunction({
                context,
                executionResult,
                requestInformation,
                serverOptions: this.options,
            })
            if (extensionsResult) {
                executionResult.extensions = extensionsResult
            }

            // Collect error metrics for execution result
            if (executionResult.errors && executionResult.errors.length > 0) {
                for (const error of executionResult.errors) {
                    if (
                        reassignAggregateError &&
                        error.originalError &&
                        isAggregateError(error.originalError)
                    ) {
                        logger.debug(
                            'Error is AggregateError and reassignAggregateError feature is enabled. AggregateError will be reassigned to original errors field.',
                            context,
                        )
                        executionResult.errors = error.originalError.errors
                    }

                    const graphqlOrFetchError =
                        determineGraphQLOrFetchError(error)
                    if (
                        graphqlOrFetchError === FETCH_ERROR &&
                        fetchErrorMessage !== undefined
                    ) {
                        error.message = fetchErrorMessage
                    }

                    logger.error(
                        executionResultErrorMessage,
                        error,
                        graphqlOrFetchError,
                        context,
                    )
                    increaseFetchOrGraphQLErrorMetric(
                        error,
                        this.options,
                        context,
                    )
                }
            }

            // Return execution result
            logger.debug(
                `Create response from data ${JSON.stringify(executionResult)}`,
                context,
            )
            return {
                executionResult,
                requestInformation: requestInformation,
                statusCode: 200,
            }
        } catch (error: unknown) {
            logger.error(
                graphqlExecutionErrorMessage,
                error as GraphQLError,
                determineGraphQLOrFetchError(error),
                context,
            )
            increaseFetchOrGraphQLErrorMetric(error, this.options, context)
            return {
                executionResult: { errors: [error as GraphQLError] },
                requestInformation: requestInformation,
                statusCode: 400,
            }
        }
    }
}

