        debugLogger.debug('test', undefined, testDateFunction)
        expect(debugLogger.logEntries.at(0)).toBe(
            `${testDateString} [DEBUG]test-logger-test-service :test`,
        )
    },
)

test.each([new TextTestLogger(), new NoStacktraceTextTestLogger()])(
