import { GraphQLError } from 'graphql'
import { getFirstErrorFromExecutionResult } from 'src'
import { expect, test } from 'vitest'

test('Should get first error from ExecutionResult', () => {
    const result = getFirstErrorFromExecutionResult({
        executionResult: {
            errors: [
                new GraphQLError('First error', {}),
                new GraphQLError('Second error', {}),
            ],
        },
    })
    expect(result.message).toBe('First error')
})

test(
    'Should return error message if no error is available when ' +
        'calling getFirstErrorFromExecutionResult',
    () => {
        const result = getFirstErrorFromExecutionResult({
            executionResult: {
                data: { response: 'doesnotmatter' },
            },
        })
        expect(result.message).toBe('No error found in ExecutionResult!')
    },
)

test(
    'Should return error message if execution result has an empty error array ' +
        'when calling getFirstErrorFromExecutionResult',
    () => {
        const result = getFirstErrorFromExecutionResult({
            executionResult: {
                errors: [],
            },
        })
        expect(result.message).toBe('No error found in ExecutionResult!')
    },
)

tests/error/DetermineGraphQLOrFetchError.source:
/* eslint-disable unicorn/error-message */
import { FETCH_ERROR, GRAPHQL_ERROR } from '@dreamit/graphql-server-base'
import { determineGraphQLOrFetchError } from 'src'
import { expect, test } from 'vitest'

const redirectedErrorMessage =
    'uri requested responds with a redirect, redirect mode is set to error'

test.each`
    errorMessage                  | expectedErrorName
    ${'ECONNREFUSED'}             | ${FETCH_ERROR}
    ${'ECONNRESET'}               | ${FETCH_ERROR}
    ${'ETIMEDOUT'}                | ${FETCH_ERROR}
    ${'network timeout'}          | ${FETCH_ERROR}
    ${'invalid redirect URL'}     | ${FETCH_ERROR}
    ${redirectedErrorMessage}     | ${FETCH_ERROR}
    ${'maximum redirect reached'} | ${FETCH_ERROR}
    ${'Cannot follow redirect'}   | ${FETCH_ERROR}
    ${'socket hang up'}           | ${FETCH_ERROR}
    ${'fetch failed'}             | ${FETCH_ERROR}
    ${'I am a GraphQLError'}      | ${GRAPHQL_ERROR}
`(
    'Correctly determine if error $errorMessage is a GraphQLError or FetchError $expectedErrorName',
    ({ errorMessage, expectedErrorName }) => {
        expect(determineGraphQLOrFetchError(new Error(errorMessage))).toBe(
            expectedErrorName,
        )
    },
)

tests/metrics/IncreaseFetchOrGraphQLErro.source:
import {
    GraphQLServerOptions,
    defaultCollectErrorMetrics,
    increaseFetchOrGraphQLErrorMetric,
} from 'src'
import { expect, test } from 'vitest'
import { JsonTestLogger } from '../TestHelpers'

test.each([
    {},
    { collectErrorMetricsFunction: defaultCollectErrorMetrics },
    { logger: new JsonTestLogger(true) },
    {
        collectErrorMetricsFunction: defaultCollectErrorMetrics,
        logger: new JsonTestLogger(true),
    },
])(
    'Test that increaseFetchOrGraphQLErrorMetric does not throw an error' +
        ' logs a debug message if a logger is defined',
    (serverOptions: GraphQLServerOptions) => {
        expect(
            increaseFetchOrGraphQLErrorMetric(undefined, serverOptions, {}),
        ).toBe(undefined)
        const { logger } = serverOptions

        if (logger && logger instanceof JsonTestLogger) {
            expect(logger.logEntries.at(0)?.message).toBe(
                'Calling increaseFetchOrGraphQLErrorMetric with error undefined and errorIsFetch false',
            )
        }
    },
)

tests/logger/CreateTimestamp.test.ts.source:
/* eslint-disable deprecation/deprecation */
import { testDateFunction, testDateString } from '@dreamit/funpara'
import { createISOTimestamp, createTimestamp } from 'src'
import { expect, test } from 'vitest'

test('CreateTimestamp should create timestamp in ISO format', () => {
    expect(createTimestamp()).toBeTruthy()
    expect(createTimestamp(new Date('2022-02-01T00:00:00.000Z'))).toBe(
        '2022-02-01T00:00:00.000Z',
    )
})

test('CreateISOTimestamp should create timestamp in ISO format', () => {
    expect(createISOTimestamp()).toBeTruthy()
    expect(createISOTimestamp(testDateFunction)).toBe(testDateString)
})

tests/logger/NoLogger.test.ts.source:
/* eslint-disable max-len */
import { NoLogger } from 'src'
import { expect, test } from 'vitest'

test('Should be able to use NoLogger without running into errors', () => {
    const logger = new NoLogger('no-logger', 'no-service')
    expect(logger.debugEnabled).toBe(false)
    const testMessage = 'Test message'

    // Then
    expect(() => logger.debug(testMessage)).not.toThrowError()
    expect(() =>
        logger.error(testMessage, new Error(testMessage)),
    ).not.toThrowError()
    expect(() => logger.info(testMessage)).not.toThrowError()
    expect(() => logger.warn(testMessage)).not.toThrowError()
})

